// YourVueComponent.vue

<template>
  <div>
    <button @click="getUser">Get User</button>
    <div v-if="userInfo">
      <h2>User Info</h2>
      <p>ID: {{ userInfo.id }}</p>
      <p>Name: {{ userInfo.name }}</p>
      <p>Is Human: {{ userInfo.isHuman }}</p>
    </div>
  </div>
</template>

<script>
import grpcClient from '../grpcClient';
import { GetRequest } from '../../../../Task_tracker_back-main/pkg/user_v1/service.pb.go';

export default {
  data() {
    return {
      userInfo: null,
    };
  },
  methods: {
    async getUser() {
      const request = new GetRequest();
      request.setId(1); // Установите необходимые параметры запроса

      try {
        const response = await grpcClient.get(request, {});
        const userInfo = response.getInfo();

        this.userInfo = {
          id: userInfo.getId(),
          name: userInfo.getName(),
          isHuman: userInfo.getIsHuman(),
        };
      } catch (error) {
        console.error('Error:', error);
      }
    },
  },
};
</script>
