// MainPage.vue

<template>
  <main>
    <div class="board-system">
      <ul class="board-list">
        <li class="board" :key="project.id" v-for="project in projects" @click="navigateToProject(project.id)">
          <h1>{{ project.name }}</h1>
          <div class="dots">
            <div class="dots-inside"></div>
            <div class="dots-inside"></div>
            <div class="dots-inside"></div>
          </div>
        </li>
        <li>
          <div class="board-adder">
            <button class="btn-board-adder" @click="addNewProject">
              <p>Создать проект +</p>
            </button>
          </div>
        </li>
      </ul>
    </div>
  </main>
</template>

<script>
import { projects } from '@/data/projects'; // Подставьте корректный путь к вашему файлу

export default {
  data() {
    return {
      projects: projects,
    };
  },
  methods: {
    addNewProject() {
      // Добавьте новый проект в массив projects
      const newProject = {
        id: this.projects.length + 1,
        name: 'Новый проект', // Можете задать начальное название
        boards: [],
      };
      this.projects.push(newProject);
    },
    navigateToProject(projectId) {
      // Перейдите на страницу проекта, передав projectId в параметрах маршрута
      this.$router.push({ name: 'BoardComp', params: { projectId } });
    },
  },
};
</script>
